        if (1/a > Math.sin(a)) 
            response.send("1/a більше ніж sin(a)");
        else if (1/a < Math.sin(a))
            response.send("sin(a) більше ніж 1/a");
        else 
        response.send("sin(a) = 1/a");
    }
});

